import { useState, useEffect } from "react";
import Image from "next/image.js";

import SearchTab from "../SearchTab/SearchTab.jsx";
import { translate } from "@/utils";

import headerImagDesktop from "@/assets/home_header.png";
import headerImagMobile from "@/assets/home_header_mobile.png";


const HeroHeader = () => {

	const [screenHeight, setScreenHeight] = useState(null);

	useEffect(() => {
		const handleResize = () => {
		  setScreenHeight(window?.innerHeight - 80);
		};
	
		// Set initial height
		handleResize();
	
		// Add event listener
		window?.addEventListener('resize', handleResize);
	
		// Cleanup event listener on unmount
		return () => {
		  window?.removeEventListener('resize', handleResize);
		};
	}, []);

	return (
		<section className="relative flex items-center justify-center w-full min-h-[580px] md:min-h-full" style={{
			height: screenHeight
		}}>
			{/* <div>
				<SliderComponent sliderData={sliderdata} />
			</div> */}

			<div className="absolute z-[1] top-1/3 -translate-y-1/3 sm:w-[70%] xl:w-[42%] 2xl:w-[36%] mx-auto">
				<h1 className="text-white text-center font-semibold mb-4 text-3xl sm-text-5xl lg:text-6xl">{translate("heroTitle")}</h1>
				<p className="text-white text-center text-lg lg:text-xl">{translate("heroSubTitle")}</p>
			</div>
			<Image
				src={headerImagDesktop}
				alt="hero home desktop"
				fill
				sizes="(max-width: 516px) 100vw, (max-width: 1440px) 50vw, 1440px"
				placeholder="blur"
				priority
				className="object-cover hidden md:block"
				// onError={placeholderImage}
				blurDataURL="data:image/webp;base64,"
			/>
			<Image
				src={headerImagMobile}
				alt="hero home desktop"
				fill
				sizes="(max-width: 516px) 100vw, (max-width: 1440px) 50vw, 1440px"
				placeholder="blur"
				priority
				className="object-cover md:hidden"
				// onError={placeholderImage}
				blurDataURL="data:image/jpeg;base64,"
			/>			

			<SearchTab getCategories={''} />
		</section>
	)
}

export default HeroHeader
